import { ActivityIndicator, Image, StyleSheet, View, } from 'react-native';
import React from 'react';
const styles = StyleSheet.create({
    container: {
        flex: 1,
        position: 'absolute',
        backgroundColor: 'transparent',
        flexDirection: 'row',
        alignItems: 'center',
        alignSelf: 'center',
        justifyContent: 'center',
        height: '100%',
        width: '100%',
    },
});
function LoadingIndicator(props) {
    const { containerStyle, renderCustomElement, style, size, color, imgSource, } = props;
    const handleImgSize = (imgSize) => {
        if (imgSize === 'large')
            return {
                width: 80,
                height: 80,
            };
        if (imgSize === 'small')
            return {
                width: 50,
                height: 50,
            };
        if (!imgSize)
            return {};
        return {
            width: imgSize,
            height: imgSize,
        };
    };
    const handleImgSourceType = (src) => {
        if (typeof src === 'string')
            return {
                uri: src,
            };
        return src;
    };
    return (<View style={StyleSheet.flatten([styles.container, containerStyle])}>
      {renderCustomElement ? (renderCustomElement()) : !imgSource ? (<ActivityIndicator style={style} size={size} color={color}/>) : (<Image source={handleImgSourceType(imgSource)} style={handleImgSize(size)}/>)}
    </View>);
}
LoadingIndicator.defaultProps = {
    size: 'large',
    color: '#969696',
};
export { LoadingIndicator };
