import React from 'react';
import styled from '@emotion/native';
const ItemWrapper = styled.TouchableOpacity({
    width: 100,
    paddingHorizontal: 12,
    paddingVertical: 14,
    justifyContent: 'center',
});
const ItemText = styled.Text `
  font-size: 12px;
  color: #2b2b2b;
`;
const SelectItem = (props) => {
    const { testID, children = '', onSelectItem = (value) => value, value, style, textStyle, } = props;
    return (<ItemWrapper testID={testID} activeOpacity={0.9} style={style} onPress={() => onSelectItem(value)}>
      <ItemText style={textStyle}>{children}</ItemText>
    </ItemWrapper>);
};
export default SelectItem;
